func guard11(age:Int?) {
    guard  age! > 18 else {
        print("guard:你没有成年，不许进入,\(age!)")
        return
    }
    print("guard:你是成年人，可以饮酒了,\(age!)")
}

guard11(age: 17)
guard11(age: 19)



 func if22(age:Int?){
    if !(age! > 18) {
        print("if:你没有成年，不许进入,\(age!)")
        return
    }
    print("if:你是成年人，可以饮酒了,\(age!)")
 }

 if22(age: 17)
 if22(age: 19)

